import React,{useState} from 'react'
import { Link } from 'react-router-dom'
import SignUpValid from './SignUpValid'

const Signup = () => {
  const [values,setValues]= useState({
    email: '',
    password: ''
})
const [errors,setErrors]= useState({})
const handleInput=(event)=> {
    setValues(prev=>({...prev,[event.target.name]:[event.target.value]}))

}

const handleSubmit=(event)=>{
    event.preventDefault();
    setErrors(SignUpValid(values));
}



  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
           <div className='bg-white p-3 rounded w-25'>
             <h2>Sign-Up</h2>
           <form action="">
                <div className='mb-3'>
                    <label htmlFor='name'><strong>Name</strong></label>
                    <input type='text' placeholder='Enter Name' className='from-control rounded-0'></input>
                </div>
               <div className='mb-3'>
                    <label htmlFor='email'><strong>Email</strong></label>
                    <input type='email' placeholder='Enter Email' className='from-control rounded-0'></input>
               </div>
               <div className='mb-3'>
                    <label htmlFor='password'><strong>Password</strong></label>
                    <input type='password' placeholder='Enter password' className='from-control rounded-0'></input>
                
               </div>
               <button className='btn btn-success w-100 rounded-0'>Sign up</button>
               <p>you are agree to our policies</p>
               <Link to='/' className='btn btn-defalut border w-100 bg-light rounded-0 text-decoration-none'>Login</Link>
            </form>
           </div>
        </div>
  )
}

export default Signup
